function arrayMin(arr) {
    return arr.reduce(function (p, v) {
        p *= 1;
        v *= 1;
        return (p < v ? p : v);
    });
};
function arrayMax(arr) {
    return arr.reduce(function (p, v) {
        p *= 1;
        v *= 1;
        return (p > v ? p : v);
    });
};
